import React, { useState } from "react";

function AddNote() {
    const [inputText, setinputText] = useState({
        title: "",
        content: "",
    })
    const [addItem, setaddItem] = useState([])

    function createNote(event) {
        const {name, value} = event.target
        setinputText(prevItem => {
            return {
                ...prevItem,
                [name]: value,
            }
        });
    };

    function newItem() {
        setaddItem((prevItem) => {
            return [...prevItem, inputItem]
        })
    }

    return (
        <div>
            <form>
            <input onChange={createNote} type="text" placeholder="Title" value={inputText.title} />
            <textarea onChange={createNote} name="content" placeholder="Take a note..." value={inputText.content} cols="30" rows="10"></textarea>
            <button onClick={newItem}>Add</button>
            </form>
        </div>
    )
}





export default AddNote;
